import { FlatList, StyleSheet, Text, View } from 'react-native';

import React from 'react';

const categories = [
    { id: '1', name: 'Breakfast' },
    { id: '2', name: 'Lunch' },
    { id: '3', name: 'Dinner' },
    { id: '4', name: 'Desserts' },
    { id: '5', name: 'Vegetarian' },
    { id: '6', name: 'Quick & Easy' },
];

const Explore = () => {
    const renderCategory = ({ item }) => (
        <View style={styles.categoryItem}>
            <Text style={styles.categoryText}>{item.name}</Text>
        </View>
    );

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Explore Recipes</Text>
            <FlatList
                data={categories}
                renderItem={renderCategory}
                keyExtractor={item => item.id}
                numColumns={2}
            />
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#f0f0f0',
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    categoryItem: {
        flex: 1,
        margin: 10,
        height: 100,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#fff',
        borderRadius: 10,
        elevation: 3,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
        shadowRadius: 2,
    },
    categoryText: {
        fontSize: 16,
        fontWeight: 'bold',
    },
});

export default Explore;
