import { ScrollView, StyleSheet, Text, View } from 'react-native';

import React from 'react';
import RecipeCard from '../components/RecipeCard';
import { colors } from '../shared/fonts';

// ... (other code remains the same)

const MyRecipes = () => {
    let popularRecipes = [
        {
            id: 1,
            name: 'Spaghetti Carbonara',
            cuisine: 'Italian',
            rating: 4.8,
            imageUrl: 'https://via.placeholder.com/300', // Replace with actual image URL
            difficulty: 'Intermediate',
            ingredients: [
                '200g spaghetti',
                '100g pancetta',
                '2 large eggs',
                '50g grated Parmesan cheese',
                '2 cloves garlic',
            ],
            instructions: 'Cook spaghetti, fry pancetta and garlic, mix eggs and cheese...',
        },
        {
            id: 2,
            name: 'Chicken Tikka Masala',
            cuisine: 'Indian',
            rating: 4.5,
            imageUrl: 'https://via.placeholder.com/300', // Replace with actual image URL
            difficulty: 'Easy',
            ingredients: [
                '500g chicken breast',
                '200g yogurt',
                '1 onion',
                '2 tomatoes',
                'Spices (cumin, coriander, garam masala, chili powder, turmeric)',
            ],
            instructions: 'Marinate chicken, cook onions and spices, add chicken and tomatoes...',
        },
        {
            id: 3,
            name: 'Sushi Rolls',
            cuisine: 'Japanese',
            rating: 4.7,
            imageUrl: 'https://via.placeholder.com/300', // Replace with actual image URL
            difficulty: 'Advanced',
            ingredients: [
                'Nori seaweed sheets',
                'Sushi rice',
                'Assorted fillings (fish, avocado, cucumber, etc.)',
                'Rice vinegar',
                'Soy sauce and wasabi (for serving)',
            ],
            instructions: 'Spread rice on nori, add fillings, roll tightly, slice and serve...',
        },
        {
            id: 4,
            name: 'Chocolate Cake',
            cuisine: 'Dessert',
            rating: 4.9,
            imageUrl: 'https://via.placeholder.com/300', // Replace with actual image URL
            difficulty: 'Moderate',
            ingredients: [
                '2 cups all-purpose flour',
                '1 3/4 cups granulated sugar',
                '3/4 cup unsweetened cocoa powder',
                '2 teaspoons baking soda',
                '1 teaspoon baking powder',
                '1 teaspoon salt',
                '2 large eggs',
                '1 cup buttermilk',
                '1 cup strong black coffee',
                '1/2 cup vegetable oil',
                '2 teaspoons vanilla extract',
            ],
            instructions: 'Mix dry ingredients, add wet ingredients, pour into pan, bake...',
        },
    ];


    return (
        <View style={styles.container}>
            <ScrollView contentContainerStyle={styles.scrollContainer}>
                {popularRecipes.map((recipe) => (
                    <RecipeCard key={recipe.id} recipe={recipe} />
                ))}
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'column',
        backgroundColor: colors.third,
        padding: 16,
    },
    secondaryText: {
        marginBottom: 10,
    },
    cardMargin: {
        marginBottom: 10,
    },
    scrollContainer: {
        paddingBottom: 20, // Adjust as needed to prevent content from sticking to the bottom
    },
});

export default MyRecipes;
