import { ImageBackground, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

import { useAppFonts } from '../shared/fonts';
import { useNavigation } from '@react-navigation/native';

export default function Landing() {
  const navigation = useNavigation();

  const loaded = useAppFonts();

  if (!loaded) {
    return null; // or a loading indicator
  }

  return (
    <ImageBackground
      source={require('../assets/images/landingPageBg.jpeg')}
      style={styles.container}
    >
      <View style={styles.contentContainer}>
        {/* App Name */}
        <Text style={styles.appName}>Fridge Chef</Text>

        {/* Greeting Text */}
        <Text style={styles.greetingText}>Capture the secrets inside your fridge, unveil a world of culinary possibilities, and let Fridge Chef guide you with mouthwatering recipes that transform everyday ingredients into extraordinary meals.</Text>
      </View>

      {/* Buttons */}
      <View style={styles.buttonContainer}>
        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate('Home')}
        >
          <Text style={styles.buttonText}>Get Started</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate('Login')}
        >
          <Text style={styles.buttonText}>Login</Text>
        </TouchableOpacity>

      </View>


    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
    alignItems: 'center',
  },
  contentContainer: {
    position: 'absolute',
    top: 70,
    left: 20,
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
  },
  appName: {
    fontSize: 40,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'PoppinsBold',
    letterSpacing: 3,
  },
  greetingText: {
    fontSize: 13,
    color: '#575c64',
    marginTop: 10,
    maxWidth: '99%',
    fontFamily: 'PoppinsLight',
    lineHeight: 25,
    padding: 5
  },
  buttonContainer: {
    position: 'absolute',
    bottom: 40,
    width: '100%',
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#fff',
    width: '43%',
    paddingVertical: 15,
    paddingHorizontal: 20,
    borderRadius: 25,
  },
  buttonText: {
    color: '#e6ae47',
    fontSize: 18,
    fontFamily: 'Poppins',
    textAlign: 'center',
  },
});
