import { FlatList, Image, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import React, { useState } from 'react';
import { baseFontSize, colors, useAppFonts } from '../shared/fonts';

import { FontAwesome } from '@expo/vector-icons';
import recipesData from '../shared/recipes.json';

const RecipeCard = ({ recipe }) => (
    <View style={styles.recipeCard}>
        <Image source={{ uri: recipe.imageUrl }} style={styles.recipeImage} />
        <View style={styles.recipeInfo}>
            <Text style={styles.recipeName}>{recipe.name}</Text>
            <Text style={styles.recipeCuisine}>{recipe.cuisine}</Text>
            <View style={styles.ratingContainer}>
                <FontAwesome name="star" size={16} color={colors.secondary} />
                <Text style={styles.ratingText}>{recipe.rating}</Text>
            </View>
        </View>
    </View>
);

const Explorer = () => {
    const [selectedCategory, setSelectedCategory] = useState(null);
    const loaded = useAppFonts();

    if (!loaded) {
        return null;
    }

    const renderCategory = ({ item }) => (
        <TouchableOpacity
            style={styles.categoryItem}
            onPress={() => setSelectedCategory(item.name)}
        >
            <Text style={styles.categoryText}>{item.name}</Text>
        </TouchableOpacity>
    );

    const filteredRecipes = selectedCategory
        ? recipesData.myRecipes.filter(recipe => recipe.category === selectedCategory)
        : [];

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Explore Recipes</Text>
            {!selectedCategory ? (
                <FlatList
                    data={recipesData.categories}
                    renderItem={renderCategory}
                    keyExtractor={item => item.id}
                    numColumns={2}
                />
            ) : (
                <View>
                    <TouchableOpacity
                        style={styles.backButton}
                        onPress={() => setSelectedCategory(null)}
                    >
                        <FontAwesome name="arrow-left" size={24} color={colors.primary} />
                        <Text style={styles.backButtonText}>Back to Categories</Text>
                    </TouchableOpacity>
                    <Text style={styles.categoryTitle}>{selectedCategory} Recipes</Text>
                    <FlatList
                        data={filteredRecipes}
                        renderItem={({ item }) => <RecipeCard recipe={item} />}
                        keyExtractor={item => item.id.toString()}
                    />
                </View>
            )}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: colors.background,
    },
    title: {
        fontFamily: 'PoppinsBold',
        fontSize: baseFontSize * 1.5,
        color: colors.text,
        marginBottom: 20,
    },
    categoryItem: {
        flex: 1,
        margin: 10,
        height: 100,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: colors.third,
        borderRadius: 10,
        elevation: 3,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
        shadowRadius: 2,
    },
    categoryText: {
        fontFamily: 'Poppins',
        fontSize: baseFontSize,
        color: colors.text,
        fontWeight: 'bold',
    },
    backButton: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 20,
    },
    backButtonText: {
        fontFamily: 'Poppins',
        fontSize: baseFontSize,
        color: colors.primary,
        marginLeft: 10,
    },
    categoryTitle: {
        fontFamily: 'PoppinsBold',
        fontSize: baseFontSize * 1.2,
        color: colors.text,
        marginBottom: 10,
    },
    recipeCard: {
        flexDirection: 'row',
        backgroundColor: colors.third,
        borderRadius: 10,
        marginBottom: 15,
        overflow: 'hidden',
    },
    recipeImage: {
        width: 100,
        height: 100,
    },
    recipeInfo: {
        flex: 1,
        padding: 10,
    },
    recipeName: {
        fontFamily: 'Poppins',
        fontSize: baseFontSize,
        color: colors.text,
    },
    recipeCuisine: {
        fontFamily: 'PoppinsLight',
        fontSize: baseFontSize * 0.8,
        color: colors.textLight,
    },
    ratingContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginTop: 5,
    },
    ratingText: {
        fontFamily: 'Poppins',
        fontSize: baseFontSize * 0.8,
        color: colors.text,
        marginLeft: 5,
    },
});

export default Explorer;
